import React from 'react';
/**
 * Enables the attaching of a custom className prop to a component. This is useful for when you
 * need toadjust the styling of an inner component that does not consume the generic className prop.
 * Usage: propAdjustedComponent = withCustomClassProp(MyComponent, myCustomClassName).
 * @param {React.Component} WrappedComponent the component to be wrapped
 * @param {string} customClassName the custom class name
 * @returns {React.Component} a wrapped component with an additional className prop
 */
declare const withCustomClassProp: <P extends {}>(WrappedComponent: React.ComponentType<P>, customClassName: string) => ({ className, ...props }: {
    className: string;
} & P) => JSX.Element;
export default withCustomClassProp;
