import React from 'react';

import { useContainerEvent } from '@wedgekit/hooks';

import type { Props } from '../Popover';

const useOutsideScroll = (ref: React.RefObject<HTMLElement>, callback: Props['onExit']) => {
  useContainerEvent(ref, {
    scroll: (e: Event, inside: boolean) => {
      if (!inside) {
        callback(e);
      }
    },
  });
};

export default useOutsideScroll;
