import React from 'react';
import type { ColorTag } from '@wedgekit/color';

import { Bar, Spinner } from './styled';

export type Props = {
  className?: string;
  color?: ColorTag;
  size?: number;
};

const Loading = ({ className, ...props }: Props) => (
  <Spinner className={className} {...props}>
    <Bar {...props} delay={-1.1} />
    <Bar {...props} delay={-1.0} />
    <Bar {...props} delay={-0.9} />
    <Bar {...props} delay={-0.8} />
    <Bar {...props} delay={-0.6} />
  </Spinner>
);

export default Loading;
