import React from 'react';

import Icon, { IconWidth, IconProps } from '@wedgekit/icons';
import type { ColorTag } from '@wedgekit/color';

import Wrapper from './styled';

interface SubmitProps extends Omit<IconProps, 'children'> {
  color?: ColorTag;
  size?: number;
}

const Submit = (props: SubmitProps) => (
  <Wrapper>
    <IconWidth iconWidth={props.size || 20}>
      <Icon {...props}>loading_circle</Icon>
    </IconWidth>
  </Wrapper>
);

export default Submit;
