import test from 'ava';
import filterArgs from './filterArgs';
test('filters out properties in the filter array', (t) => {
    t.deepEqual(filterArgs(['--foo', 'bar'], ['foo']), []);
    t.deepEqual(filterArgs(['--foo', 'bar', '--baz', 'qux'], ['foo']), ['--baz', 'qux']);
    t.deepEqual(filterArgs(['--foo', 'bar', '--baz', 'qux'], ['foo', 'baz']), []);
    t.deepEqual(filterArgs(['--foo-bar', 'bar', '--baz', 'qux'], ['foo-bar', 'baz']), []);
});
test("doesn't filter out properties not in the filter array", (t) => {
    t.deepEqual(filterArgs(['--foo', 'bar'], ['baz']), ['--foo', 'bar']);
    t.deepEqual(filterArgs(['--foo', 'bar', '-b'], ['foo']), ['-b']);
    t.deepEqual(filterArgs(['--foo-bar', 'bar', '-b'], ['foo-bar']), ['-b']);
});
